import React from "react";
import './BoardsDetail.css'
import addImage from "../../../asset/add_image.png";
import { useState } from "react";

function BoardsDetail() {
  // 입력값 저장하는 상태 설정하기
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  // 입력값에 문제가 있을 경우 보여줄 에러메세지 저장하는 상태 설정하기
  const [nameError, setNameError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [titleError, setTitleError] = useState("");
  const [contentError, setContentError] = useState("");
  const [isActive, setIsActive] = useState(false);

  // 변경값 확인하여 상태에 저장하기
  const onChangeName = (event) => {
    setName(event.target.value);

		// early-exit 패턴
		if (event.target.value && password && title && content) return setIsActive(true);

		setIsActive(false)
  };

  const onChangePassword = (event) => {
    setPassword(event.target.value);

    if (name && event.target.value && title && content) return setIsActive(true);

		setIsActive(false)
  };

  const onChangeTitle = (event) => {
    setTitle(event.target.value);

    if (name && password && event.target.value && content) return setIsActive(true);

		setIsActive(false)
  };

  const onChangeContent = (event) => {
    setContent(event.target.value);

    if (name && password && title && event.target.value) return setIsActive(true);

		setIsActive(false)
  };

  // 등록하기 버튼 눌렀을때 실행되는 함수
  const onClickSignup = () => {
    // 0. 에러상태 초기화
    let hasError = false;

    // 1. 작성자 이름 검증하기
    if (name.trim() === "") {
      setNameError("필수입력 사항입니다.");
      hasError = true;
    } else {
      setNameError("");
    }
    // 2. 비밀번호 검증하기
    if (password.length === 0) {
      setPasswordError("필수입력 사항입니다.");
      hasError = true;
    } else {
      setPasswordError("");
    }

    // 3. 제목 검증하기
    if (title.trim() === "") {
      setTitleError("필수입력 사항입니다.");
      hasError = true;
    } else {
      setTitleError("");
    }
    // 4. 내용 검증하기
    if (content.trim() === "") {
      setContentError("필수입력 사항입니다.");
      hasError = true;
    } else {
      setContentError("");
    }
    // 5. 성공알람 보여주기
    if (hasError === false) {
      alert("게시글 등록이 가능한 상태입니다!");
    }
  };

  return (
<></>
  );
}

export default BoardsDetail;
